  ColorSwatchPickerItem,
  ColorThumb,
  defaultColor
} from './color'
import { ColorField } from './color-field'
import { Dialog } from './dialog'
import { DynamicOverlay } from './dynamic-overlay'
import { Description, Label } from './field'
import { cn } from './primitive'
import { Select, SelectItem } from './select'

interface ColorPickerProps extends ColorPickerPrimitiveProps {
  space?: ColorSpace
  enableColorSwatch?: boolean
  enableColorFormatSelection?: boolean
  isHex?: boolean
  label?: string
  description?: string
  errorMessage?: string
  placeholder?: string
  colors?: string[]
  isDisabled?: boolean
  className?: string
  trigger?: 'color-picker' | 'color-field'
  enableColorField?: boolean
}

const ColorPicker = ({
  enableColorSwatch = false,
  enableColorFormatSelection = false,
  enableColorField = true,
  label,
  description,
  colors = [...Object.values(tailwindColors.zinc)],
  placeholder = 'Fill Color',
  isHex = true,
  isDisabled = false,
  className,
  trigger = 'color-picker',
  ...props
}: ColorPickerProps) => {
  const [space, setSpace] = React.useState<ColorSpace>('rgb')
  const [isHexFormat, setIsHexFormat] = React.useState(false)
  const value = props.value ?? props.defaultValue

  return (
    <Group className={cn('flex w-full flex-col gap-1', className)}>
      {label && <Label>{label}</Label>}
      <ColorPickerPrimitive defaultValue={defaultColor} {...props}>
        <DialogTrigger>
          {trigger === 'color-field' ? (
            <ButtonPrimitive className="size-10 -mr-2.5 grid place-content-center focus:outline-none">
              <ColorSwatch color={value?.toString(space)} className="size-6" />
            </ButtonPrimitive>
          ) : trigger === 'color-picker' ? (
            <Button
              aria-label="Open color picker"
              isDisabled={isDisabled}
              appearance="outline"
              className="w-full max-w-sm justify-start"
            >
              <ColorSwatch color={value?.toString(space)} className="-ml-1.5 size-6" />
              {value ? <span>{value.toString(space)}</span> : <span>{placeholder}</span>}
            </Button>
          ) : null}
          <DynamicOverlay
            placement="bottom start"
            role="dialog"
            className="w-full p-0 overflow-hidden min-w-full sm:w-fit sm:min-w-fit"
          >
            <Dialog
              className={twJoin(
                '[[data-placement]>&]:p-[0.70rem] lg:w-[18rem] lg:p-0',
                enableColorField && 'space-y-2'
              )}
            >
              <div>
                <ColorArea
                  className="w-full sm:w-[calc(18rem-1.30rem)]"
                  colorSpace="hsb"
                  xChannel="saturation"
                  yChannel="brightness"
                  aria-label="Color area"
                >
                  <ColorThumb className="z-50" />
                </ColorArea>
                <ColorSlider
                  showOutput={false}
                  className="mt-2 [&_.cstrk]:orientation-horizontal:h-3"
                  colorSpace="hsb"
                  channel="hue"
                  aria-label="Color slider"
                />
              </div>

              <div className="grid gap-2">
                {enableColorFormatSelection && (
                  <Select
                    aria-label="Select color format"
                    selectedKey={space}
                    onSelectionChange={(s) => {
                      setSpace(s as ColorSpace)
                      setIsHexFormat(s === 'hex')
                    }}
                  >
                    {['rgb', 'hex', 'hsl', 'hsb'].map((s) => (
                      <SelectItem key={s} id={s} textValue={s}>
                        {s}
                      </SelectItem>
                    ))}
                  </Select>
                )}

                {enableColorFormatSelection ? (
                  <div className="flex gap-1 w-[inherit]">
                    {isHexFormat ? (
                      <ColorField aria-label="Hex color" colorSpace={space} />
                    ) : (
                      getColorChannels(space).map((channel) => (
                        <ColorField
                          aria-label={channel.toString()}
                          colorSpace={space}
                          channel={channel}
                          key={channel}
                        />
                      ))
                    )}
                  </div>
                ) : enableColorField ? (
                  <ColorField aria-label={`Color in ${space} format`} colorSpace={space} />
                ) : null}
              </div>

              {enableColorSwatch && colors && colors.length > 0 && (
                <ColorSwatchPicker layout="grid" className="flex flex-wrap gap-x-2 gap-y-2.5">
                  {colors.map((color) => (
                    <ColorSwatchPickerItem key={color} color={color} />
                  ))}
                </ColorSwatchPicker>
              )}
            </Dialog>
          </DynamicOverlay>
        </DialogTrigger>
      </ColorPickerPrimitive>
      {description && <Description>{description}</Description>}
    </Group>
  )
}

export { ColorPicker, type ColorPickerProps }

snippet.raw:
'use client'

import * as React from 'react'

import { cn } from '@/components/ui/primitive'
import { IconCheck, IconDuplicate } from '@irsyadadl/paranoid'
import { AnimatePresence, motion } from 'framer-motion'
import { toast } from 'sonner'
import { twMerge } from 'tailwind-merge'

import { Button, type ButtonProps } from './button'

const snippetVariants = {
  hidden: { opacity: 0, scale: 0.5 },
  visible: { opacity: 1, scale: 1 }
}

interface SnippetProps extends React.HTMLAttributes<HTMLDivElement> {
  text: string
}

const Snippet: React.FC<SnippetProps> = ({ className, text, ...props }) => {
  const [copied, setCopied] = React.useState(false)

  const handleCopy = async () => {
    if (navigator.clipboard && window.isSecureContext) {
      try {
        await navigator.clipboard.writeText(text)
        setCopied(true)
        setTimeout(() => setCopied(false), 2000) // Reset the copied state after 2 seconds
      } catch (error) {
        toast.error('Failed to copy to clipboard')
      }
    } else {
      toast.error('Failed to copy to clipboard')
    }
  }

  return (
    <div
      {...props}
      className={twMerge(
        'relative flex items-center justify-between rounded-lg border bg-[#0e0e10] text-white py-2.5 pl-3 pr-2.5 font-mono text-sm [&>svg:hover]:text-white [&>svg]:text-muted-fg [&>svg]:transition [&_svg]:shrink-0',
        className
      )}
    >
      <span className="mr-6">{text}</span>
      <Button
        className="size-7 bx backdrop-blur-lg text-white bg-zinc-800 border hover:bg-zinc-700 border-zinc-700"
        aria-label="Copy imports statement"
        size="square-petite"
        appearance="outline"
        onPress={handleCopy}
      >
        <AnimatePresence mode="wait" initial={false}>
          {copied ? (
            <motion.span key="checkmark" variants={snippetVariants} initial="hidden" animate="visible" exit="hidden">
              <IconCheck />
            </motion.span>
          ) : (
            <motion.span key="copy" variants={snippetVariants} initial="hidden" animate="visible" exit="hidden">
              <IconDuplicate />
            </motion.span>
          )}
        </AnimatePresence>
      </Button>
    </div>
  )
}

interface CopyButtonProps extends ButtonProps {
  isCopied?: boolean
  ariaLabel?: string
  initialIcon?: React.ReactNode
  copiedIcon?: React.ReactNode
  className?: string
}

const CopyButton = ({
  initialIcon,
  copiedIcon,
  ariaLabel = 'Copy',
  isCopied,
  className,
  ...props
}: CopyButtonProps) => {
  return (
    <Button
      className={cn(
        'size-7 backdrop-blur-lg text-white bg-zinc-800 border hover:bg-zinc-700 border-zinc-700',
        className
      )}
      aria-label={ariaLabel}
      size="square-petite"
      appearance="outline"
      {...props}
    >
      <AnimatePresence mode="wait" initial={false}>
        {isCopied ? (
          <motion.span
            key="checkmark-import"
            variants={snippetVariants}
            initial="hidden"
            animate="visible"
            exit="hidden"
          >
            {copiedIcon ?? <IconCheck />}
          </motion.span>
        ) : (
          <motion.span key="copy-import" variants={snippetVariants} initial="hidden" animate="visible" exit="hidden">
            {initialIcon ?? <IconDuplicate />}
          </motion.span>
        )}
      </AnimatePresence>
    </Button>
  )
}

export { CopyButton, Snippet, snippetVariants, type SnippetProps }

number-field.raw:
'use client'

import { IconChevronDown, IconChevronUp, IconMinus, IconPlus } from '@irsyadadl/paranoid'
import {
  Button,
  type ButtonProps,
  NumberField as NumberFieldPrimitive,
  type NumberFieldProps as NumberFieldPrimitiveProps,
  type ValidationResult
} from 'react-aria-components'

import { Description, fieldBorderStyles, FieldError, FieldGroup, Input, Label } from './field'
import { cn, ctr, useMediaQuery } from './primitive'

interface NumberFieldProps extends NumberFieldPrimitiveProps {
  label?: string
  description?: string
  placeholder?: string
  errorMessage?: string | ((validation: ValidationResult) => string)
}

const NumberField = ({ label, placeholder, description, errorMessage, ...props }: NumberFieldProps) => {
  const isMobile = useMediaQuery('(max-width: 768px)')
  return (
    <NumberFieldPrimitive {...props} className={ctr(props.className, 'group flex flex-col gap-1')}>
      <Label>{label}</Label>
      <FieldGroup className="group-disabled:bg-secondary">
        {(renderProps) => (
          <>
            {isMobile ? <StepperButton slot="decrement" className="border-r" /> : null}
            <Input placeholder={placeholder} />
            <div
              className={fieldBorderStyles({
                ...renderProps,
                className: 'grid h-10 place-content-center border-s'
              })}
            >
              {isMobile ? (
                <StepperButton slot="increment" />
              ) : (
                <div className="flex h-full flex-col">
                  <StepperButton slot="increment" emblemType="chevron" className="h-5 px-1" />
                  <div
                    className={fieldBorderStyles({
                      ...renderProps,
                      className: 'border-b'
                    })}
                  />
                  <StepperButton slot="decrement" emblemType="chevron" className="h-5 px-1" />
                </div>
              )}
            </div>
          </>
        )}
      </FieldGroup>
      {description && <Description>{description}</Description>}
      <FieldError>{errorMessage}</FieldError>
    </NumberFieldPrimitive>
  )
}

interface StepperButtonProps extends ButtonProps {
  slot: 'increment' | 'decrement'
  emblemType?: 'chevron' | 'default'
  className?: string
}

const StepperButton = ({ slot, className, emblemType = 'default', ...props }: StepperButtonProps) => {
  const icon =
    emblemType === 'chevron' ? (
      slot === 'increment' ? (
        <IconChevronUp className="size-5" />
      ) : (
        <IconChevronDown className="size-5" />
      )
    ) : slot === 'increment' ? (
      <IconPlus />
    ) : (
      <IconMinus />
    )
  return (
    <Button
      className={cn(
        'h-10 cursor-default px-2 text-muted-fg pressed:bg-primary pressed:text-primary-fg group-disabled:bg-secondary forced-colors:group-disabled:text-[GrayText]',
        className
      )}
      slot={slot}
      {...props}
    >
      {icon}
    </Button>
  )
}

export { NumberField, type NumberFieldProps }

table.raw:
'use client'

import { IconChevronDown, IconChevronUp, IconDotGrid2X3 } from '@irsyadadl/paranoid'
import {
  Button,
  Cell,
  type CellProps,
  Collection,
  Column,
  type ColumnProps,
  Row,
  type RowProps,
  Table as TablePrimitive,
  TableBody as TableBodyPrimitive,
  type TableBodyProps,
  TableHeader as TableHeaderPrimitive,
  type TableHeaderProps,
  type TableProps as TablePrimitiveProps,
  useTableOptions
} from 'react-aria-components'
import { tv } from 'tailwind-variants'

import { Checkbox } from './checkbox'
import { cn } from './primitive'

const table = tv({
  slots: {
    root: 'table w-full caption-bottom border-spacing-0 text-sm outline-none',
    column: 'whitespace-nowrap px-4 py-3 text-left font-medium outline-none [&:has([slot=selection])]:pr-0',
    header: 'border-b',
    row: 'tr group relative cursor-default border-b text-fg/70 outline-none ring-primary focus-visible:ring-1 selected:bg-primary/15',
    cell: 'whitespace-nowrap px-4 py-3 outline-none'
  }
})

const { root, header, column, row, cell } = table()

const TableBody = <T extends object>(props: TableBodyProps<T>) => (
  <TableBodyPrimitive {...props} className={cn('[&_.tr:last-child]:border-0')} />
)

interface TableProps extends TablePrimitiveProps {
  className?: string
}

const Table = ({ children, className, ...props }: TableProps) => (
  <div className="relative w-full overflow-auto">
    <TablePrimitive {...props} className={root({ className })}>
      {children}
    </TablePrimitive>
  </div>
)

const TableCell = ({ children, className, ...props }: CellProps & { className?: string }) => (
  <Cell {...props} className={cell({ className })}>
    {children}
  </Cell>
)

const TableColumn = ({ children, className, ...props }: ColumnProps & { className?: string }) => (
  <Column isRowHeader {...props} className={column({ className })}>
    {({ allowsSorting, sortDirection }) => (
      <div className="flex items-center gap-2">
        <>
          {children}
          {allowsSorting &&
            (sortDirection === undefined ? (
              <div className="w-6"></div>
            ) : sortDirection === 'ascending' ? (
              <IconChevronUp />
            ) : (
              <IconChevronDown />
            ))}
        </>
      </div>
    )}
  </Column>
)

const TableHeader = <T extends object>({
  children,
  className,
  columns,
  ...props
}: TableHeaderProps<T> & { className?: string }) => {
  const { selectionBehavior, selectionMode, allowsDragging } = useTableOptions()
  return (
    <TableHeaderPrimitive {...props} className={header({ className })}>
      {allowsDragging && <Column />}
      {selectionBehavior === 'toggle' && (
        <Column className="pl-4">{selectionMode === 'multiple' && <Checkbox slot="selection" />}</Column>
      )}
      <Collection items={columns}>{children}</Collection>
    </TableHeaderPrimitive>
  )
}

const TableRow = <T extends object>({
  children,
  className,
  columns,
  id,
  ...props
}: RowProps<T> & { className?: string }) => {
  const { selectionBehavior, allowsDragging } = useTableOptions()
  return (
    <Row
      id={id}
      {...props}
      className={row({
        className: 'href' in props ? cn('cursor-pointer hover:bg-secondary/50', className) : ''
      })}
    >
      {allowsDragging && (
        <Cell className="ring-primary">
          <Button
            className="cursor-pointer bg-transparent p-1.5 text-muted-fg pressed:cursor-grab pressed:text-fg"
            slot="drag"
          >
            <IconDotGrid2X3 className="size-5" />
          </Button>
        </Cell>
      )}
      {selectionBehavior === 'toggle' && (
        <Cell className="pl-4">
          <span aria-hidden className="absolute inset-y-0 left-0 hidden h-full w-0.5 bg-primary group-selected:block" />
          <Checkbox slot="selection" />
        </Cell>
      )}
      <Collection items={columns}>{children}</Collection>
    </Row>
  )
}

export { Table, TableBody, TableCell, TableColumn, TableHeader, TableRow, type TableProps }

dynamic-overlay.raw:
'use client'

import * as React from 'react'

import type { DialogProps, PopoverProps } from 'react-aria-components'
import { Modal } from 'react-aria-components'
import type { VariantProps } from 'tailwind-variants'
import { tv } from 'tailwind-variants'

import { ModalClose } from './modal'
import { PopoverPicker } from './popover'
import { cn, useMediaQuery } from './primitive'

const drawerStyles = tv({
  base: [
    'fixed bg-popover bottom-0 top-auto z-50 w-full max-w-xl place-content-center gap-4 rounded-t-xl border border-b-transparent shadow-lg outline-none',
    'entering:animate-in entering:fade-in-0 entering:slide-in-from-bottom-1/2 entering:[transition-timing-function:ease-out',
    'exiting:animate-out exiting:fade-out-0 exiting:slide-out-to-bottom-1/2 exiting:[transition-timing-function:ease]'
  ]
})

interface OverlayProps
  extends Omit<DialogProps, 'children' | 'className' | 'style'>,
    Omit<PopoverProps, 'children' | 'className' | 'style'>,
    Omit<VariantProps<typeof drawerStyles>, 'className'> {
  className?: string | DialogProps['className'] | PopoverProps['className']
  children: React.ReactNode
}

const DynamicOverlay = ({ className, children, ...props }: OverlayProps) => {
  const isMobile = useMediaQuery('(max-width: 600px)')

  return isMobile ? (
    <Modal {...props} isDismissable className={cn(drawerStyles(), className)}>
      {children}

      <div className="pb-4 px-4 mx-auto w-full max-w-[inherit]">
        <ModalClose shape="circle" className="w-full">
          Close
        </ModalClose>
      </div>
    </Modal>
  ) : (
    <PopoverPicker {...props} className={className}>
      {children}
    </PopoverPicker>
  )
}

export { DynamicOverlay }

