    <ColorFieldPrimitive {...props} className={ctr(props.className, 'group w-full flex flex-col gap-1')}>
      {label && <Label>{label}</Label>}
      <FieldGroup data-loading={isLoading ? 'true' : undefined} className={fieldGroupPrefixStyles()}>
        {prefix ? <span className="atrs isPfx">{prefix}</span> : null}
        <div className="flex items-center">
          {value && (
            <>
              {enableColorPicker ? (
                <ColorPicker
                  enableColorField={false}
                  onChange={props.onChange}
                  defaultValue={value}
                  className="size-9"
                  trigger="color-field"
                />
              ) : (
                <ColorSwatch className="size-6 ml-2" color={value.toString()} />
              )}
            </>
          )}

          <Input className="px-2.5" placeholder={placeholder} />
        </div>
        {suffix ? <span className="atrs ml-auto isSfx">{suffix}</span> : null}
      </FieldGroup>
      {description && <Description>{description}</Description>}
      <FieldError>{errorMessage}</FieldError>
    </ColorFieldPrimitive>
  )
}

export { ColorField }

