'use client'

import jsonPreviews from '@/components/docs/generated/previews.json'
import { Code } from '@/components/docs/rehype/code'
import { cn } from '@/lib/utils'
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from '@radix-ui/react-collapsible'
import * as React from 'react'
import { Button } from 'ui'

interface SourceCodeProps extends React.HTMLAttributes<HTMLDivElement> {
  toShow: string
}

export function SourceCode({ toShow, ...props }: SourceCodeProps) {
  const [codeString, setCodeString] = React.useState('')
  const [isOpened, setIsOpened] = React.useState(false)
  React.useMemo(() => {
    // @ts-ignore
    const componentData = jsonPreviews[toShow]
    if (componentData) {
      const updatedRawContent = componentData.raw.replace(
        /export default function \w+\(\) \{/g,
        'export default function App() {'
      )
      setCodeString(updatedRawContent)
    } else {
      console.error('Component not found:', toShow)
    }
  }, [toShow])
  return (
    <section className="my-6 not-prose">
      <p className="mb-4 -mt-2">
        And next, you can copy the code below and paste it into your dopest component folder.
      </p>
      <div className={cn('overflow-hidden rounded-md')}>
        <Collapsible open={isOpened} onOpenChange={setIsOpened}>
          <div className={'relative overflow-hidden'} {...props}>
            <CollapsibleContent forceMount className={cn('overflow-hidden', !isOpened && 'h-32')}>
              <div
                className={cn(
                  '[&_pre]:my-0 [&_pre]:max-h-[32rem] [&_pre]:pb-[100px]',
                  !isOpened ? '[&_pre]:overflow-hidden' : '[&_pre]:overflow-auto]'
                )}
              >
                <Code code={codeString} />
              </div>
            </CollapsibleContent>
            <div
              className={cn(
                'absolute flex items-center justify-center bg-gradient-to-b from-popover/50 to-background',
                isOpened ? 'inset-x-0 bottom-0 h-12' : 'inset-0'
              )}
            >
              <CollapsibleTrigger asChild>
                <Button intent="secondary" size="small">
                  {isOpened ? 'Collapse' : 'Expand'}
                </Button>
              </CollapsibleTrigger>
            </div>
          </div>
        </Collapsible>
      </div>
    </section>
  )
}

rehype/code.raw:
'use client'

import { IconCircleInfo } from '@irsyadadl/paranoid'
import React from 'react'
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter'
import { nightOwl as theme } from 'react-syntax-highlighter/dist/esm/styles/prism'
import { cn, CopyButton } from 'ui'
import { copyToClipboard } from 'usemods'

function Code({ lang = 'tsx', code }: { lang?: string; code: string }) {
  const [copied, setCopied] = React.useState<string>('')

  function copyImportsToClipboard(): void {
    const importRegex = /import[\s\S]+?from\s+['"][\s\S]+?['"];?\n*/g
    const _imports = code.match(importRegex) || []
    const imports = _imports.map((importStatement: string) => importStatement.trim()).join('\n')
    copyToClipboard(imports)
      .then(() => {
        setCopied('imports')
        setTimeout(() => setCopied(''), 2000)
      })
      .catch((err) => console.error('Copy failed: ', err))
  }

  return (
    <div className="dfakdpxe2941 not-prose group relative max-h-96 overflow-y-auto rounded-lg [&_pre]:!bg-[#0e0e10] [&_pre_code]:!leading-loose [&_pre]:!m-0 [&_pre]:!p-4 border-zinc-800 border font-mono text-sm">
      <div className={cn('absolute z-20 bottom-auto right-3 top-3 flex gap-1.5')}>
        <CopyRawButton code={code} />
        <CopyButton initialIcon={<IconCircleInfo />} isCopied={copied === 'imports'} onPress={copyImportsToClipboard} />
      </div>
      <SyntaxHighlighter language={lang} style={theme}>
        {code}
      </SyntaxHighlighter>
    </div>
  )
}

export function CopyRawButton({ code }: { className?: string; code: any }) {
  const [copied, setCopied] = React.useState<string>('')
  const copyRaw = () => {
    copyToClipboard(code)
      .then(() => {
        setCopied('raw')
        setTimeout(() => setCopied(''), 2000)
      })
      .catch((err) => {
        console.error('Copy failed: ', err)
      })
  }
  return <CopyButton isCopied={copied === 'raw'} onPress={copyRaw} />
}

export { Code }

toc.raw:
'use client'

import React, { Suspense } from 'react'
import { Heading, Menu, MenuItem } from 'react-aria-components'
import { cn, Skeleton } from 'ui'

export function Toc({ items }: { items: any[] }) {
  const ids = items.map((item) => item.url.split('#')[1])
  const activeId = useActiveItem(ids)
  return (
    <div>
      <aside className="hidden xl:sticky xl:top-[1.75rem] xl:-mr-6 xl:block xl:h-[calc(100vh-4.75rem)] xl:flex-none xl:overflow-y-auto xl:py-16 xl:pr-6">
        <nav aria-labelledby="on-this-page-title" className="w-56">
          <Suspense
            fallback={
              <div className="space-y-2">
                <Skeleton className="h-3 w-20 animate-pulse" />
                <Skeleton className="h-3 w-32 animate-pulse" />
                <Skeleton className="h-3 w-12 animate-pulse bg-fg/50" />
                <Skeleton className="h-3 w-16 animate-pulse" />
                <Skeleton className="h-3 w-8 animate-pulse" />
                <Skeleton className="h-3 w-24 animate-pulse" />
              </div>
            }
          >
            <>
              <Heading level={2} className="text-base lg:text-lg font-semibold leading-7 mb-6 text-fg">
                On this page
              </Heading>
              <Menu className="flex flex-col gap-y-2">
                {items.map((item) => (
                  <MenuItem
                    key={item.title}
                    className={cn('outline-none text-muted-fg lg:text-sm', {
                      'text-primary font-medium': item.url.split('#')[1] === activeId
                    })}
                    id={item.title}
                    href={item.url}
                  >
                    {item.title}
                  </MenuItem>
                ))}
              </Menu>
            </>
          </Suspense>
        </nav>
      </aside>
    </div>
  )
}

function useActiveItem(itemIds: string[]) {
  const [activeId, setActiveId] = React.useState<string | null>(null)

  React.useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        let bestCandidate: IntersectionObserverEntry | null = null
        entries.forEach((entry) => {
          if (entry.isIntersecting && (!bestCandidate || bestCandidate.intersectionRatio < entry.intersectionRatio)) {
            bestCandidate = entry
          }
        })
        if (bestCandidate) {
          // @ts-ignore
          setActiveId(bestCandidate.target.id)
        }
      },
      { rootMargin: '0% 0% -80% 0%', threshold: 0.5 }
    )

    itemIds.forEach((id) => {
      const element = document.getElementById(id)
      if (element) observer.observe(element)
    })

    return () => {
      itemIds.forEach((id) => {
        const element = document.getElementById(id)
        if (element) observer.unobserve(element)
      })
    }
  }, [itemIds, activeId])

  return activeId
}

