tag-group.raw:
'use client'

import { IconX } from '@irsyadadl/paranoid'
import { createContext, useContext } from 'react'
import {
  Button,
  composeRenderProps,
  TagGroup as TagGroupPrimitive,
  type TagGroupProps as TagGroupPrimitiveProps,
  TagList,
  type TagListProps,
  Tag as TagPrimitive,
  type TagProps as TagPrimitiveProps,
  Text
} from 'react-aria-components'
import { twMerge } from 'tailwind-merge'
import { tv } from 'tailwind-variants'
import { badgeIntents, badgeStyles } from './badge'
import { Description, Label } from './field'
import { cn, focusRing } from './primitive'

type Intent = keyof typeof badgeIntents
const IntentContext = createContext<Intent>('primary')

const emptyColors = Object.keys(badgeIntents).reduce(
  (acc, key) => {
    acc[key] = ''
    return acc
  },
  {} as Record<string, string>
)
const tagStyles = tv({
  base: [badgeStyles.base, 'cursor-pointer focus:outline-none select-none disabled:cursor-default'],
  variants: {
    intent: {
      ...emptyColors
    },
    shape: {
      ...badgeStyles.variants.shape
    },
    allowsRemoving: {
      true: 'pr-1'
    },
    isSelected: {
      true: 'border-transparent bg-primary-600 text-white forced-color-adjust-none forced-badgeIntents:bg-[Highlight] forced-badgeIntents:text-[HighlightText]'
    },
    isDisabled: {
      true: 'opacity-60 forced-badgeIntents:text-[GrayText]'
    }
  },

  defaultVariants: {
    shape: 'square'
  },
  compoundVariants: (Object.keys(badgeIntents) as Intent[]).map((intent) => ({
    isSelected: false,
    intent,
    className: badgeIntents[intent]
  }))
})

export interface TagGroupProps<T>
  extends Omit<TagGroupPrimitiveProps, 'children'>,
    Pick<TagListProps<T>, 'items' | 'children' | 'renderEmptyState'> {
  intent?: Intent
  label?: string
  description?: string
  errorMessage?: string
}

export interface TagProps extends TagPrimitiveProps {
  intent?: Intent
}

export function TagGroup<T extends object>({
  label,
  description,
  errorMessage,
  items,
  children,
  renderEmptyState,
  ...props
}: TagGroupProps<T>) {
  return (
    <TagGroupPrimitive {...props} className={twMerge('flex flex-col gap-1', props.className)}>
      <Label>{label}</Label>
      <IntentContext.Provider value={props.intent || 'primary'}>
        <TagList items={items} renderEmptyState={renderEmptyState} className="flex flex-wrap gap-1">
          {children}
        </TagList>
      </IntentContext.Provider>
      {description && <Description>{description}</Description>}
      {errorMessage && (
        <Text slot="errorMessage" className="text-sm text-danger">
          {errorMessage}
        </Text>
      )}
    </TagGroupPrimitive>
  )
}

const removeButtonStyles = tv({
  extend: focusRing,
  base: 'flex cursor-default items-center justify-center rounded-full p-0.5 transition-[background-color] hover:bg-black/10 pressed:bg-black/20 dark:hover:bg-white/10 dark:pressed:bg-white/20'
})

export function Tag({ children, intent, ...props }: TagProps) {
  let textValue = typeof children === 'string' ? children : undefined
  let groupIntent = useContext(IntentContext)
  return (
    <TagPrimitive
      textValue={textValue}
      {...props}
      className={composeRenderProps(props.className, (className, renderProps) =>
        tagStyles({
          ...renderProps,
          className: cn('href' in props ? '' : 'focus:ring-1 focus:ring-primary-400', className),
          intent: intent || groupIntent
        })
      )}
    >
      {({ allowsRemoving }) => (
        <>
          {children}
          {allowsRemoving && (
            <Button slot="remove" className={removeButtonStyles}>
              <IconX aria-hidden className="h-3 w-3" />
            </Button>
          )}
        </>
      )}
    </TagPrimitive>
  )
}

sheet.raw:
'use client'

import * as React from 'react'
import {
  Button,
  type DialogProps,
  DialogTrigger as DialogTriggerPrimitive,
  Modal,
  ModalOverlay,
  type ModalOverlayProps
} from 'react-aria-components'
import { tv, type VariantProps } from 'tailwind-variants'
import { Dialog } from './dialog'
import { CloseButtonIndicator, ModalClose, ModalDescription, ModalFooter, ModalHeader, ModalTitle } from './modal'
import { cn } from './primitive'

const Sheet = DialogTriggerPrimitive
const SheetTrigger = Button
const SheetClose = ModalClose
const SheetFooter = ModalFooter
const SheetHeader = ModalHeader
const SheetTitle = ModalTitle
const SheetDescription = ModalDescription

const generateCompoundVariants = (sides: Array<'top' | 'bottom' | 'left' | 'right'>) => {
  return sides.map((side) => ({
    side,
    isStack: true,
    className:
      side === 'top'
        ? 'top-2 inset-x-2 rounded-lg border'
        : side === 'bottom'
          ? 'bottom-2 inset-x-2 rounded-lg border'
          : side === 'left'
            ? 'left-2 inset-y-2 rounded-lg border'
            : 'right-2 inset-y-2 rounded-lg border'
  }))
}

const sheetVariants = tv({
  base: 'fixed z-50 grid gap-4 bg-popover text-popover-fg shadow-lg transition ease-in-out entering:duration-300 entering:animate-in exiting:duration-200 exiting:animate-out',
  variants: {
    side: {
      top: 'inset-x-0 top-0 rounded-b-2xl border-b entering:slide-in-from-top exiting:slide-out-to-top',
      bottom: 'inset-x-0 bottom-0 rounded-t-2xl border-t entering:slide-in-from-bottom exiting:slide-out-to-bottom',
      left: 'inset-y-0 left-0 h-auto w-72 sm:w-3/4 overflow-y-auto border-r entering:slide-in-from-left exiting:slide-out-to-left sm:max-w-xs',
      right:
        'inset-y-0 right-0 h-auto w-72 sm:w-3/4 overflow-y-auto border-l entering:slide-in-from-right exiting:slide-out-to-right sm:max-w-xs'
    },
    isStack: {
      true: '',
      false: ''
    }
  },
  compoundVariants: generateCompoundVariants(['top', 'bottom', 'left', 'right'])
})

const SheetOverlay = ({ className, isDismissable = true, ...props }: ModalOverlayProps) => (
  <ModalOverlay
    isDismissable={isDismissable}
    className={(values) =>
      cn(
        'fixed inset-0 z-50 bg-black/50 entering:animate-in entering:fade-in-0 exiting:duration-300 exiting:animate-out exiting:fade-out-0',
        typeof className === 'function' ? className(values) : className
      )
    }
    {...props}
  />
)

export interface SheetContentProps
  extends Omit<React.ComponentProps<typeof Modal>, 'children'>,
    VariantProps<typeof sheetVariants> {
  children?: DialogProps['children']
  role?: DialogProps['role']
  closeButton?: boolean
  isStack?: boolean
}

const SheetContent = (props: SheetContentProps) => {
  const { className, children, side = 'right', role, closeButton = true, isStack = true } = props
  return (
    <Modal className={cn(sheetVariants({ side, isStack }), className)} {...props}>
      <Dialog aria-label="Sheet" role={role} className="h-full outline-none">
        {(values) => (
          <>
            {typeof children === 'function' ? children(values) : children}
            {closeButton && <CloseButtonIndicator className="top-2.5 right-2.5" close={values.close} />}
          </>
        )}
      </Dialog>
    </Modal>
  )
}

export {
  Sheet,
  SheetClose,
  SheetContent,
  SheetDescription,
  SheetFooter,
  SheetHeader,
  SheetOverlay,
  SheetTitle,
  SheetTrigger
}

