  { id: 1, name: 'The Beatles' },
  { id: 2, name: 'Led Zeppelin' },
  { id: 3, name: 'Pink Floyd' },
  { id: 4, name: 'Queen' },
  { id: 5, name: 'The Rolling Stones' },
  { id: 6, name: 'The Beach Boys' },
  { id: 7, name: 'The Kinks' },
  { id: 8, name: 'The Who' }
]

